import './contact.css'
import CardModules from '../card/card'

const Contact = () =>(
    <div className="mail">
        <article className='mailForm'>
            <section className="mailFormContainer">
                <h3 className='mailFormTitle'>Nombre</h3>
                <input className='mailFormBox'>

                </input>
            </section>
            <section className="mailFormContainer">
                <h3 className='mailFormTitle'>Email</h3>
                <input className='mailFormBox'>

                </input>
            </section>
            <section className="mailFormContainer">
                <h3 className='mailFormTitle'>Descripcion</h3>
                <input className='mailFormBox'>

                </input>
            </section>
        </article>
    </div>
)

const ModalContact = () =>{

    const [modal, setModal] = useState(false);

    const toggleModal = () => {
        setModal(!modal);
    };

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    return (
        <>
            <div className="modalPresentation" onClick={toggleModal}>
                <button className="modalButton">
                    <div className='modalPrincipal'><img src={props.image} alt=''/></div>
                    <p className="modalText">{props.resume}</p>
                </button>   
            </div>

        {modal && (
            <div className="modalContainer">
                <div className="modal">
                    <div onClick={toggleModal} className="overlay"></div>
                        <div className="modal-content">
                            <h2>{props.resume}</h2>
                            <div className="modalList">{props.children}
                            </div>
                        <button className="close-modal" onClick={toggleModal}>
                            CLOSE
                        </button>
                    </div>
                </div>
            </div>
        )}
        </>
    );
}


export default Mail
