import './slider.css'
import React, { useRef, useState } from "react";

import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/navigation";
import { Navigation } from "swiper";



const Card = ({image, descripton, resume}) =>(
    <button>
        <div>
            <div className='CardImage'><img src={image} alt={resume} /></div>
            <div className='Description'>{descripton}</div>
        </div>
    </button>
)
const Slider = () =>(
    <>
        <div className='SliderContainer'>
            <div className='SliderMoveBack'><a href="/#" id='BackLink' onClick="aheadSlide(-1)">&#10094;</a></div>
            <div className='SliderCardContainer'>
            <Swiper navigation={true} modules={[Navigation]} className="mySwiper">
                <Card
                    image='chrome://branding/content/about-logo.png'
                    resume='reutrteo'
                    descripton='arutranurauntraonutranutraoenturanturaoentur'
                />
            </Swiper>
            </div>
            <div className='SliderMoveAhead'><a href="/#" id='AheadLink' onClick="aheadSlide(1)">&#10095;</a></div>
        </div>    
        <div className='SliderPositions'>
                <span className='position Active' onClick="positionSlide(1)"></span>
                <span className='Position' onClick="positionSlide(2)"></span>
                <span className='Position' onClick="positionSlide(3)"></span>
                <span className='Position' onClick="positionSlide(4)"></span>
                <span className='Position' onClick="positionSlide(5)"></span>
                <span className='Position' onClick="positionSlide(6)"></span>     
        </div>
    </>
)
let indice = 1;
showSlides(indice);

function aheadSlide(n){
    showSlides( indice+=n );
}

function positionSlide(n){
    showSlides(indice=n);
}

function showSlides(n){
    let i;
    let slides = document.getElementsByClassName('card');
    let position = document.getElementsByClassName('position');

    if(n > slides.length){
        indice = 1;
    }
    if(n < 1){
        indice = slides.length;
    }

    for(i = 0; i < slides.length; i++){
        slides[i].style.display = 'none';
    }

    for(i = 0; i < position.length; i++){
        position[i].className = position[i].className.replace(" active", "");
    }



}

export default Slider
