import './slider.css'

const Card = ({image, descripton, resume}) =>(
    <button>
        <div className='CardContainer'>
            <div className='CardImage'><img src={image} alt={resume} /></div>
            <div className='Description'>{descripton}</div>
        </div>
    </button>
)

const Slider = () =>(
    <div className='Slider'>
        <Card
            image="chrome://branding/content/about-logo.png"
            resume="tnarunatru"
            descripton="natruntarountar"
        />
        <div class="SliderMove">
            <a href="#" class="Back" onclick="bacakSlide(-1)">&#10094;</a>
            <a href="#" class="Ahead" onclick="aheadSlide(1)">&#10095;</a>
        </div>
        <div class="SliderPosition">
            <span class="PositionActive" onclick="positionSlide(1)"></span>
            <span class="Position" onclick="positionSlide(2)"></span>
            <span class="Position" onclick="positionSlide(3)"></span>
        </div>
    </div>
)


let indice = 1;
ShowSlides(indice);

function avanzaSlide(n){
    ShowSlides( indice+=n );
}

function posicionSlide(n){
    ShowSlides(indice=n);
}
setInterval(function tiempo(){
    ShowSlides(indice+=1)
},4000);
function ShowSlides(n){
    let i;
    let cards = document.getElementsByClassName('CardContainer');
    let position = document.getElementsByClassName('position');

    if(n > cards.length){
        indice = 1;
    }
    if(n < 1){
        indice = cards.length;
    }
    for(i = 0; i < cards.length; i++){
        cards[i].style.display = 'none';
    }
    for(i = 0; i < position.length; i++){
        position[i].className = position[i].className.replace(" active", "");
    }

    cards[indice-1].style.display = 'block';
    position[indice-1].className += ' active';

}



export default Slider
